#!/usr/bin/perl

if ( @ARGV < 2 )  {
    die "Usage: $0 combined_dta.txt human\|mouse\|rat\|all\n";
}
$dta=$ARGV[0];
$sterm=$ARGV[1];
$suffix=$sterm."_SHs";

&defshs;   #this subroutine converts ##shs## keywords into lists of serine-hydrolases

open(INFILE, $dta ) || die "cannot open $dta\n";
$outfile = $dta."_".$suffix.".txt";
open(OUTFILE,">$outfile") || die "cannot write to $outfile\n";

while(<INFILE>) {
    chomp();
    @words = split(/\t/);
if ($words[1] =~ /^IPI/) {
    @ipi = split(/\./,$words[1]);
    if ( $ipi[0] =~ m/$sterm/i ) {
	print OUTFILE $_, "\tTRUE\n";
    } else {
	print OUTFILE $_, "\tFALSE\n";
    }
} elsif ( $words[0] eq "index" ) {
    print OUTFILE $_,"\t", $suffix,"\n";
} else {
    print OUTFILE $_,"\t \n";
}
}
close(INFILE);
close(OUTFILE);

